import React, { useEffect, useRef, useState } from "react";
import EmojiPicker from "emoji-picker-react";
import { comment } from "../../functions/post";

const CreateComments = ({ user, postId }) => {
  const [picker, setPicker] = useState(false);
  const [text, setText] = useState("");
  const [cursorPosition, setCursorPosition] = useState();
  const imgRef = useRef(null);
  const textRef = useRef(null);
  const [commentImage, setCommentImage] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    textRef.current.selectionEnd = cursorPosition;
  }, [cursorPosition]);

  const handleEmoji = (e) => {
    const ref = textRef.current;
    ref.focus();
    const start = text.substring(0, ref.selectionStart);
    const end = text.substring(ref.selectionStart);
    const newText = start + e.emoji + end;
    setText(newText);
    console.log(start, start.length, end);
    setCursorPosition(start.length + e.emoji.length);
  };

  const handleImage = (e) => {
    let file = e.target.files[0];

    if (
      file.type !== "image/jpeg" &&
      file.type !== "image/png" &&
      file.type !== "image/webp" &&
      file.type !== "image/gif"
    ) {
      setError(
        `${file.name} frormat is unsupported ! only Jpeg, PNG and GIF images are supported`
      );

      return;
    } else if (file.size > 1024 * 1024 * 5) {
      setError(`${file.name} size is too large, max size 5mb allowed`);

      return;
    }

    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = (event) => {
      setCommentImage(event.target.result);
    };
  };

  const handleComment = async (e) => {
    if (e.key === "Enter") {
      if (commentImage != "") {
        setLoading(true);

        const postImages = images.map((img) => {
          return dataURItoBlob(img);
        });

        const path = `${user.username}/post_images`;
        let formData = new FormData();
        formData.append("path", path);

        postImages.forEach((image) => {
          formData.append("file", image);
        });

        const response = await uploadImages(formData, path, user.token);

        setLoading(false);
      } else {
        const comments = await comment(postId, text, "", user.token);
        console.log(comments);
      }
    }
  };

  return (
    <div className="create_comment">
      <img src={user?.picture} alt="" />
      <div className="comment_input_wrap">
        {picker && (
          <div className="comment_emoji_picker">
            <EmojiPicker onEmojiClick={handleEmoji} height={350} />
          </div>
        )}
        <input
          type="file"
          hidden
          ref={imgRef}
          onChange={handleImage}
          accept="image/png, image/jpeg, image/gif, image/webp, image/ico"
        />

        {error && (
          <div className="postError comment_error">
            <div className="postError_error"> {error}</div>
            <button className="blue_btn" onClick={() => setError("")}>
              Try again
            </button>
          </div>
        )}

        <input
          type="text"
          ref={textRef}
          value={text}
          onChange={(e) => setText(e.target.value)}
          onKeyUp={handleComment}
        />
        <div
          className="comment_circle_icon hover2"
          onClick={() => setPicker(!picker)}
        >
          <i className="emoji_icon"></i>
        </div>
        <div
          className="comment_circle_icon hover2"
          onClick={() => imgRef.current.click()}
        >
          <i className="camera_icon"></i>
        </div>
        <div className="comment_circle_icon hover2">
          <i className="gif_icon"></i>
        </div>
        <div className="comment_circle_icon hover2">
          <i className="sticker_icon"></i>
        </div>
      </div>
      {commentImage && (
        <div className="comment_img_preview">
          <img src={commentImage} alt="" />
          <div
            className="small_white_circle"
            onClick={() => setCommentImage("")}
          >
            <i className="exit_icon"></i>
          </div>
        </div>
      )}
    </div>
  );
};

export default CreateComments;
