import React, { useRef } from "react";
import EmojiPickerBackground from "./EmojiPickerBackground";

const ImagePreview = ({ text, setText, user, images, setImages }) => {
  const imageInputRef = useRef(null);
  const handleImage = () => {
    imageInputRef.current.click();
  };
  return (
    <div className="overflow_a">
      <EmojiPickerBackground user={user} text={text} setText={setText} type2 />
      <div className="add_pics_wrap">
        <input
          type="file"
          multiple
          hidden
          ref={imageInputRef}
          onChange={handleImage}
        />
        {images && images.length ? (
          ""
        ) : (
          <div className="add_pics_inside">
            <div className="small_white_circle">
              <i className="exit_icon"></i>
            </div>
            <div className="add_col">
              <div className="add_circle">
                <i className="addPhoto_icon"></i>
              </div>
            </div>
          </div>
        )}
        ;
      </div>
    </div>
  );
};

export default ImagePreview;
