import { useRef, useState } from "react";
import "./style.css";

const ProfilePicture = () => {
  const refInput = useRef(null);
  const [image, setImage] = useState(null);
  const [error, setError] = useState(null);

  const handleImage = (e) => {
    let file = e.target.files[0];

    if (
      file.type !== "image/jpeg" &&
      file.type !== "image/png" &&
      file.type !== "image/webp" &&
      file.type !== "image/gif"
    ) {
      setError(
        `${file.name} frormat is unsupported ! only Jpeg, PNG and GIF images are supported`
      );

      return;
    } else if (file.size > 1024 * 1024 * 5) {
      setError(`${file.name} size is too large, max size 5mb allowed`);

      return;
    }

    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = (event) => {
      setImage(event.target.result);
    };
  };

  return (
    <div className="blur">
      <input type="file" ref={refInput} hidden onChange={handleImage} />
      <div className="postBox pictureBox">
        <div className="box_header">
          <div className="small_circle">
            <div className="exit_icon"></div>
          </div>
          <span>Update profile picture</span>
        </div>
        <div className="update_picture_wrap">
          <div className="update_picture_buttons">
            <button className="light_blue_btn">
              <i className="plus_icon filter_blue"></i>
              Upload photo
            </button>
            <button className="gray_btn">
              <i className="frame_icon"></i>
              Add frame
            </button>
          </div>
        </div>
        {error && (
          <div className="postError comment_error">
            <div className="postError_error"> {error}</div>
            <button className="blue_btn" onClick={() => setError("")}>
              Try again
            </button>
          </div>
        )}
        <div className="old_picture_wrap"></div>
      </div>
    </div>
  );
};

export default ProfilePicture;
