import React from "react";
import Header from "../../components/header/Header";

import LeftHome from "../../components/home/left/LeftHome";
import { useSelector } from "react-redux";
import RightHome from "../../components/home/right/RightHome";
import Stories from "../../components/home/stories/Stories";
import "./style.css";
import CreatePost from "../../components/createPost/CreatePost";
import SendVerification from "../../components/home/sendVerification/SendVerification";
import Post from "../../components/post/Post";

const Home = ({ setVisible, posts }) => {
  const { user } = useSelector((state) => ({ ...state }));

  console.log(posts);
  return (
    <div className="home">
      <Header />
      <LeftHome user={user} />
      <div className="home_middle">
        <Stories />
        {user.verified === false && <SendVerification user={user} />}

        <CreatePost user={user} setVisible={setVisible} />

        <div className="posts">
          {console.log(posts)}
          {posts.map((post) => {
            <Post key={post._id} post={post} />;
          })}
        </div>
      </div>
      <RightHome user={user} />
    </div>
  );
};

export default Home;
