import React, { useRef, useState } from "react";
import Cropper from "react-easy-crop";

const UpdateProfilePicture = ({ setImage, image }) => {
  const [description, setDescription] = useState("");

  const [crop, setCrop] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const refSlider = useRef(null);

  const onCropComplete = (croppedArea, croppedAreaPixels) => {
    console.log(croppedArea, croppedAreaPixels);
  };

  const zoomIn = () => {
    refSlider.current.stepUp();
    setZoom(refSlider.current.value);
  };
  const zoomOut = () => {
    refSlider.current.stepDown();
    setZoom(refSlider.current.value);
  };

  return (
    <div className="postBox update_img">
      <div className="box_header">
        <div className="small_circle">
          <div className="exit_icon" onClick={() => setImage("")}></div>
        </div>
        <span>Update profile picture</span>
      </div>
      <div className="update_image_desc">
        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.targetivalue)}
          className="textarea_blue details_input"
        ></textarea>
      </div>
      <div className="update_center">
        <div className="cooper">
          <Cropper
            image={image}
            crop={crop}
            zoom={zoom}
            aspect={1 / 1}
            cropShape="round"
            onCropChange={setCrop}
            onCropComplete={onCropComplete}
            onZoomChange={setZoom}
            showGrid={false}
          />
        </div>
        <div className="slider">
          <div className="slider_circle hover1" onClick={() => zoomOut()}>
            <i className="minus_icon"></i>
          </div>
          <input
            type="range"
            min={1}
            max={3}
            step={0.2}
            value={zoom}
            ref={refSlider}
            onChange={(e) => setZoom(e.target.value)}
          />
          <div className="slider_circle hover1" onClick={() => zoomIn()}>
            <i className="plus_icon"></i>
          </div>
        </div>
        <div className="flex_up">
          <div className="gray_btn">
            <i className="crop_icon"></i>
            Crop photo
          </div>
        </div>
      </div>
    </div>
  );
};

export default UpdateProfilePicture;
