import { useEffect, useRef, useState } from "react";
import EmojiPicker, { EmojiStyle } from "emoji-picker-react";

const EmojiPickerBackground = ({ text, user, setText }) => {
  const [picker, setPicker] = useState(false);

  const [cursorPosition, setCursorPosition] = useState();
  useEffect(() => {
    textRef.current.selectionEnd = cursorPosition;
  }, [cursorPosition]);
  const handleEmoji = (e) => {
    console.log(e.emoji);
    const ref = textRef.current;
    ref.focus();
    const start = text.substring(0, ref.selectionStart);
    const end = text.substring(ref.selectionStart);
    const newText = start + e.emoji + end;
    setText(newText);
    setCursorPosition(start.length + e.emoji.length);
  };
  return (
    <>
      <div className="flex_center">
        <textarea
          ref={textRef}
          maxLength="100"
          value={text}
          placeholder={`What's on your mind, ${user.first_name}`}
          className="post_input"
          onChange={(e) => setText(e.target.value)}
        ></textarea>
      </div>
      <div className="post_emojis_wrap">
        {picker && (
          <div className="comment_emoji_picker rlmove">
            <EmojiPicker onEmojiClick={handleEmoji} height={350} width={300} />
          </div>
        )}
        <img src="../../../icons/colorful.png" alt="" />
        <i
          className="emoji_icon_large"
          onClick={() => {
            setPicker((prev) => !prev);
          }}
        ></i>
      </div>
    </>
  );
};

export default EmojiPickerBackground;
