import React, { useEffect, useRef, useState } from "react";
import EmojiPicker from "emoji-picker-react";

const CreateComments = ({ user }) => {
  const [picker, setPicker] = useState(false);
  const [text, setText] = useState("");
  const [cursorPosition, setCursorPosition] = useState();
  const imgRef = useRef(null);
  const textRef = useRef(null);
  const [commentImage, setCommentImage] = useState("");

  useEffect(() => {
    textRef.current.selectionEnd = cursorPosition;
  }, [cursorPosition]);

  const handleEmoji = (e) => {
    const ref = textRef.current;
    ref.focus();
    const start = text.substring(0, ref.selectionStart);
    const end = text.substring(ref.selectionStart);
    const newText = start + e.emoji + end;
    setText(newText);
    console.log(start, start.length, end);
    setCursorPosition(start.length + e.emoji.length);
  };

  const handleImage = (e) => {
    let file = e.target.files[0];

    if (
      img.type !== "image/jpeg" &&
      img.type !== "image/png" &&
      img.type !== "image/webp" &&
      img.type !== "image/gif"
    ) {
    }

    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = (event) => {
      setCommentImage(event.target.result);
    };
  };

  return (
    <div className="create_comment">
      <img src={user?.picture} alt="" />
      <div className="comment_input_wrap">
        {picker && (
          <div className="comment_emoji_picker">
            <EmojiPicker onEmojiClick={handleEmoji} height={350} />
          </div>
        )}
        <input type="file" hidden ref={imgRef} />
        <input
          type="text"
          ref={textRef}
          value={text}
          onChange={handleImage}
          accept="image/png, image/jpeg, image/gif, image/webp, image/ico"
        />
        <div
          className="comment_circle_icon hover2"
          onClick={() => setPicker(!picker)}
        >
          <i className="emoji_icon"></i>
        </div>
        <div
          className="comment_circle_icon hover2"
          onClick={() => imgRef.current.click()}
        >
          <i className="camera_icon"></i>
        </div>
        <div className="comment_circle_icon hover2">
          <i className="gif_icon"></i>
        </div>
        <div className="comment_circle_icon hover2">
          <i className="sticker_icon"></i>
        </div>
      </div>
    </div>
  );
};

export default CreateComments;
