import React, { useRef, useState } from "react";
import Header from "../../components/header/Header";
import useClickOutside from "../../components/helpers/useClickOutside";
import LeftHome from "../../components/home/left/LeftHome";
import { useSelector } from "react-redux";
import RightHome from "../../components/home/right/RightHome";
import Stories from "../../components/home/stories/Stories";
import "./style.css";
import CreatePost from "../../components/createPost/CreatePost";
import ActivateForm from "./ActivateForm";

const Activate = () => {
  const { user } = useSelector((state) => ({ ...state }));

  const [success, setSuccess] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  return (
    <div className="home">
      <ActivateForm type="success" header="Account verification succeded" />
      <Header />
      <LeftHome user={user} />
      <div className="home_middle">
        <Stories />
        <CreatePost user={user} />
      </div>
      <RightHome user={user} />
    </div>
  );
};

export default Activate;
