import { useRef, useState } from "react";
import useClickOutside from "../../components/helpers/useClickOutside";

const Friendship = () => {
  const [friendsMenu, setFriendsMenu] = useState(false);
  const [respondMenu, setResponsesMenu] = useState(false);

  const menu = useRef(null);
  const menu1 = useRef(null);
  useClickOutside(menu, () => setFriendsMenu(false));
  useClickOutside(menu1, () => setResponsesMenu(false));

  const friendship = {
    friends: false,
    following: true,
    requestSent: false,
    requestReceived: true,
  };

  return (
    <div className="friendship">
      {friendship.friends ? (
        <div className="friends_menu_wrap">
          <button className="gray_btn" onClick={() => setFriendsMenu(true)}>
            <img src="../../../icons/friends.png" alt="" />
            <span>Friends</span>
          </button>
          {friendsMenu && (
            <div className="open_cover_menu" ref={menu}>
              <div className="open_cover_menu_item hover1">
                <img src="../../../icons/favoritesOutline.png" alt="" />
                Favorites
              </div>
              <div className="open_cover_menu_item hover1">
                <img src="../../../icons/editFriends.png" alt="" />
                Edit Friend List
              </div>
              {friendship.following ? (
                <div className="open_cover_menu_item hover1">
                  <img src="../../../icons/unfollowOutlined.png" alt="" />
                  unfollow
                </div>
              ) : (
                <div className="open_cover_menu_item hover1">
                  <img src="../../../icons/unfollowOutlined.png" alt="" />
                  Follow
                </div>
              )}
              <div className="open_cover_menu_item hover1">
                <i className="unfriend_outlined_icon"></i>
                unFriend
              </div>
            </div>
          )}
        </div>
      ) : (
        !friendship.requestReceived &&
        !friendship.requestSent && (
          <button className="blue_btn">
            <img src="../../../icons/addFriend.png" alt="" className="invert" />
            <span>Add Friend</span>
          </button>
        )
      )}
      {friendship.requestSent ? (
        <button className="blue_btn">
          <img
            src="../../../icons/cancelRequest.png"
            alt=""
            className="invert"
          />
          <span>Cancel Request</span>
        </button>
      ) : (
        friendship.requestReceived && (
          <div className="friends_menu_wrap" ref={menu1}>
            <button className="gray_btn" onClick={() => setResponsesMenu(true)}>
              <img src="../../../icons/friends.png" alt="" />
              <span>Respond</span>
            </button>
            {respondMenu && (
              <div className="open_cover_menu" ref={menu}>
                <div className="open_cover_menu_item hover1">Confirm</div>
                <div className="open_cover_menu_item hover1">Delete</div>
              </div>
            )}
          </div>
        )
      )}
      {friendship.following ? (
        <button className="gray_btn">
          <img src="../../../icons/follow.png" alt="" />
          <span>Following</span>
        </button>
      ) : (
        <button className="blue_btn">
          <img src="../../../icons/follow.png" alt="" className="invert" />
          <span>Follow</span>
        </button>
      )}
      <button className="blue_btn">
        <img src="../../../icons/message.png" alt="" className="invert" />
        <span>Message</span>
      </button>
    </div>
  );
};

export default Friendship;
