import Cookies from "js-cookie";
console.log(Cookies.get("user"));

export function userReducer(
  state = Cookies.get("user") ? JSON.parse(Cookies.get("user")) : "fehmi",

  action
) {
  switch (action.type) {
    case "LOGIN":
      return action.payload;

    default:
      return state;
  }
}

export default userReducer;
